import { Canvas } from '@react-three/fiber'
import { OrbitControls } from '@react-three/drei'
import { EffectComposer, Bloom } from '@react-three/postprocessing'


function Box(props) {
    const { position, geometry } = props
    return (
        <mesh  position={position}>
            <EffectComposer multisampling={8}>
                <Bloom kernelSize={3} luminanceThreshold={0.2} luminanceSmoothing={0.4} intensity={1} />
            </EffectComposer>
            <boxGeometry args={geometry}/>
            <meshStandardMaterial />
        </mesh>
    )
}

export default function App() {
    return (
        <div id="canvas-container" style={{ width: '100%', height: '500px', color: 'grey' }}>
            <Canvas camera={{ position: [1, 1, 5] }} >
                <color attach="background" args={['#25282b']} />
                <OrbitControls enableZoom={true} enablePan={false} enableRotate={true} />
                <ambientLight intensity={0.2} />
                <directionalLight color="green" position={[1, 3, 5]} />
                <Box position={[0, 0, -5]} geometry={[10, 1, 1]}/>
                <Box position={[0, 0, 5]} geometry={[10, 1, 1]}/>
            </Canvas>
        </div>
    )
}

